
public class main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i;
		double f;
		f = 1 / 5;
		System.out.println(f);
		f = (double) 1 / 5;
		System.out.println(f);
		i = (int) 1.7 + (int) 1.8;
		System.out.println(i);
		
		int a;
		a = (int) 3.5 + (int) 3.7;
		System.out.println(a);
		
		double sum = 1.3 + 12;
		System.out.println(sum);
		
		//int sum_ = 1.3 + 12;
		//System.out.println(sum_);
		
	}

}
